let hr=document.querySelector("#hr"),mn=document.querySelector("#mn"),sc=document.querySelector("#sc");setInterval(()=>{var e=new Date,t=30*e.getHours(),r=6*e.getMinutes(),e=6*e.getSeconds();hr.style.transform=`rotateZ(${t+r/12}deg)`,r.style.transform=`rotateZ(${r}deg)`,sc.style.transform=`rotateZ(${e}deg)`});